import React, { Component } from "react";
import ReactDOM from "react-dom";
import { createShaderCanvas } from "react-shader-canvas";
import './index.css';

const shader = props =>
  `#ifdef GL_ES
  precision mediump float;
  #endif

  uniform float u_time;
  uniform vec2 u_resolution;
  uniform vec2 u_mouse;

  void main(){
      vec2 coord = gl_FragCoord.xy / u_resolution;
      vec3 color = vec3(0.0);
      vec2 translate = vec2(-0.5);
      coord += translate;

      color.r += abs(0.1 + length(coord) - 0.6 * abs(sin(u_time * 0.9 / 12.0)));
      color.g += abs(0.1 + length(coord) - 0.6 * abs(sin(u_time * 0.6 / 4.0)));
      color.b += abs(0.1 + length(coord) - 0.6 * abs(sin(u_time * 0.8 / 9.0)));


      gl_FragColor = vec4(0.05 / color, 1.0);
  }`;

const ShaderComponent = createShaderCanvas(shader);

class App extends Component {
  state = {
    temps: 0.1
  };

  handleCLick = () => this.setState(state => ({ temps: state.temps + 0.1 }));

  render() {
    return (
      <div onMouseOver={this.handleCLick}>
        <ShaderComponent
          style={{ width: "100%", height: "100vh" }}
          temps={this.state.temps}
        />
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
