import React, { useState } from "react";
import ReactDOM from "react-dom";
import './index.css';
import ShaderComponent from "./shaders/oblivion"

 const App = () => {
    const [temps, setTemps] = useState(0.1);

    const handleTime = () => setTimeout(setTemps(temps + 0.1), 500);
    return (
        <div onMouseOver={handleTime}>
        <ShaderComponent
          style={{ width: "100%", height: "100vh" }}
          temps={temps}
        />
      </div>
    )
}
export default App

